 (chatMessages: ChatMessage[]) => {
  const messagesWithTokenCount = calculateMessagesTokenCount(chatMessages);

  // newest first
  const reversed = messagesWithTokenCount.reverse();

  // remove old stuff

  const filteredOld = reversed.filter((item, index) => {
    if (index <= 9) {
      // keep 10 newest always
      return true;
    }
    const isNew = Date.now() - item.createdAt < 86400000;
    if (isNew) {
      return true;
    }
    return false;
  });

  // remove everything before last 3000 tokens
  const index = filteredOld.findIndex((item, index, array) => {
    const totalTokens = sum(array.slice(0, index).map((x) => x.tokenCount));
    if (totalTokens > 3000) return true;
  });
  const sliced = index === -1 ? filteredOld : filteredOld.slice(0, index);
  const truncatedMessages: ChatMessage[] = sliced
    .reverse()
    .map(({ tokenCount, ...item }) => item);

  const inputTokensCalculated = sum(sliced.map((x) => x.tokenCount));

  return { truncatedMessages, inputTokensCalculated };
}
