value:
(person: Person, persona: Persona) => {
  const dailyActiveUsers = 0;

  const personStatus =
    persona.assistantState?.minimumCreditVip &&
    (person.credit || 0) > persona.assistantState?.minimumCreditVip
      ? "vip"
      : persona.assistantState?.minimumCreditPaid &&
        (person.credit || 0) > persona.assistantState.minimumCreditPaid
      ? "paid"
      : "regular";

  const dauLimit =
    personStatus === "vip" && persona.assistantState?.dauLimitVip
      ? persona.assistantState.dauLimitVip
      : personStatus === "paid" && persona.assistantState?.dauLimitPaid
      ? persona.assistantState.dauLimitPaid
      : persona.assistantState?.dauLimit || 100;

  const tooManyDau = dailyActiveUsers > dauLimit;

  const freeMessagesAmountPersona = tooManyDau
    ? persona.assistantState?.freeMessagesPerNewUserAfterDauLimit
    : persona.assistantState?.freeMessagesPerNewUser;

  const freeMessagesAmount = freeMessagesAmountPersona || 0;

  return { tooManyDau, freeMessagesAmount, dailyActiveUsers };
}
