async () => {
  const stats = await getPersonaStatistics();

  const message = `
Total users: ${stats.totalPersons}
Daily active users (DAU): ${stats.activePersonsAmount}
New users: ${stats.newPersonsAmount}
Returning users: ${stats.returningPersonsAmount}
Messages sent today: ${stats.messagesTodayAmount}

Statistics per Chat AI:

${Object.values(stats.personaStatistics)
  .map((item) => {
    return `- **${item.name}**: ${item.activeUsersTodayAmount} DAU, of which ${item.newUsersTodayAmount} new, sent ${item.newMessagesTodayAmount} messages`;
  })
  .join("\n")}
`;

  const staff = (await db.get("Person")).filter((x) =>
    x.groupSlugs?.includes("cfa-freelancers")
  );

  const res = staff.map((person) =>
    sendMessage(person, message, {
      emailConfig: { subject: `Daily stats (${getCurrentDate()})` },
    })
  );
}
