 () => {
  const [isMobileMenuEnabled, setIsMobileMenuEnabled] = useStore(
    "menu.isMobileMenuEnabled"
  );
  const queryPath = useQueryPath();
  const { customProjectRelativeBaseFolderPath } =
    useCustomProjectRelativeBaseFolderPath();
  const navigationQuery = queries.useGetNavigationWithContext({
    queryPath,
    customProjectRelativeBaseFolderPath,
  });

  const {
    projectRelativeFilePath,
    projectRelativeFolderPath,
    queryIsFolder,
    navigation,
    isSuccessful,
    message,
    canRead,
    canWrite,
  } = destructureOptionalObject(navigationQuery.data?.result);

  const filename = queryIsFolder
    ? undefined
    : getFileOrFolderName(projectRelativeFilePath);

  const basePathsQuery = queries.useGetExplorableBasePathsWithContext();
  const explorableBasePaths = basePathsQuery.data?.result;

  const explorableBasePathsActive = explorableBasePaths?.map(
    (explorableBasePath) => {
      const href = `/${explorableBasePath.projectRelativePath}`;
      const isActive = queryPath.startsWith(
        explorableBasePath.projectRelativePath
      );

      return { ...explorableBasePath, href, isActive };
    }
  );

  /**
   * On top, render the explorable base paths, with the one you're in as active
   */
  const renderExplorableBasePaths = explorableBasePathsActive?.map(
    (explorableBasePath) => {
      const { href, isActive, name } = explorableBasePath;
      return (
        <NavButton
          key={`explorable${name}`}
          href={href}
          title={name}
          isActive={isActive}
        />
      );
    }
  );

  const renderThisFolder =
    isSuccessful === false ? (
      <P>{message}</P>
    ) : (
      navigation?.map((navItem) => {
        // if it's a file, we need to remove the name

        const hrefChunks = [projectRelativeFolderPath, navItem.name].filter(
          notEmpty
        );
        const hrefUrl = "/" + path.join(...hrefChunks);
        const href = hrefUrl.startsWith("/") ? hrefUrl : `/${hrefUrl}`;

        const extraIcons = [
          navItem.isDraft ? "ğŸ§ª" : undefined,
          navItem.isPrivate ? "ğŸ”‘" : undefined,
          navItem.isSecret ? "ğŸ”’" : undefined,
          // navItem.authorizedGroup === "premium"
          //   ? "ğŸ›"
          //   : navItem.authorizedGroup === "enterprise"
          //   ? "ğŸš€"
          //   : undefined,
        ]
          .filter(notEmpty)
          .join("");

        const extension = navItem.name.split(".").pop()!;
        const extensionIcons = {
          ts: "âŒ˜",
          tsx: "âŒ˜",
          json: "ğŸ’¿",
          md: "ğŸ“„",
        };
        const icon = navItem.isFileCollection
          ? "ğŸ—"
          : navItem.type === "file"
          ? Object.keys(extensionIcons).includes(extension)
            ? extensionIcons[extension as keyof typeof extensionIcons]
            : "ğŸ“„"
          : "ğŸ“‚";

        const name = navItem.isFileCollection
          ? withoutSubExtensions(navItem.name)
          : navItem.name;

        return (
          <NavButton
            onClick={() => {
              if (navItem.type === "folder") {
                return;
              }
              setIsMobileMenuEnabled(false);
            }}
            isActive={filename === navItem.name}
            href={href}
            id={href}
            title={`${icon} ${name} ${extraIcons}`}
          />
        );
      })
    );

  /**
   * Render everything
   *
   */
  return (
    <Div className="w-full lg:h-full lg:overflow-y-scroll">
      <div className="flex flex-col w-full">
        {navigationQuery.isLoading ? <FancyLoader /> : null}
        {/* root */}
        {renderExplorableBasePaths}
        {/* Add favorite folders here */}
        <FoldersPiece
          queryPath={projectRelativeFilePath || queryPath}
          explorableBasePathsActive={explorableBasePathsActive}
          filename={filename}
          isFolder={queryIsFolder}
        />
        {renderThisFolder}
      </div>
    </Div>
  );
}
