 async (
  functionContext: FunctionContext,
  config: {
    /**
     * If defined, queryPath should be relative to this one. Should only be allowed for admin.
     */
    customProjectRelativeBaseFolderPath?: string;
    /**
     * QueryPath as in the URL, should be a relative path from the projectRoot (or to `customAbsoluteBaseFolderPath`, if defined)
     */
    queryPath: string;
  }
): Promise<{
  isSuccessful: boolean;
  message?: string;
  navigation?: FolderContent[];
  canRead?: boolean;
  canWrite?: boolean;
  projectRelativeFolderPath?: string;
  projectRelativeFilePath?: string;
  queryIsFolder?: boolean;
}> => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) {
    return { isSuccessful: false, message: "No projectroot" };
  }
  const executionId = generateUniqueId();
  const performance: (PerformanceItem | undefined)[] = [];
  getNewPerformance("start", executionId, true);

  const { customProjectRelativeBaseFolderPath, queryPath } = config;
  const queryInfo = await getQueryInfo(config);

  if (!queryInfo.isSuccessful || !queryInfo.result) {
    return { isSuccessful: false, message: queryInfo.message };
  }

  const { projectRelativeFolderPath, projectRelativeFilePath, queryIsFolder } =
    queryInfo.result;
  const absoluteFolderPath = path.join(projectRoot, projectRelativeFolderPath);
  // NB: should include public and inferred groups...
  const groupSlugs = functionContext.groups?.map((x) => x.slug) || [];

  const authorization = await getFsAuthorizationWithContext(
    functionContext,
    projectRelativeFolderPath
  );

  if (!authorization.canRead) {
    return { isSuccessful: false, message: "You have no access" };
  }

  performance.push(getNewPerformance("before", executionId));

  const navigation = await getFolderFileCollections(
    absoluteFolderPath,
    projectRoot,
    groupSlugs
  );

  performance.push(getNewPerformance("navigation", executionId));

  //console.log({ performance, queryPath });
  cleanupTimer(executionId);

  return {
    isSuccessful: true,
    navigation,
    canRead: authorization.canRead,
    canWrite: authorization.canWrite,
    projectRelativeFolderPath,
    projectRelativeFilePath,
    queryIsFolder,
  };
}
