value:
(props: {
  /**
   * File to apply the actions on
   */
  projectRelativeFilePath: string;
}) => {
  const { projectRelativeFilePath } = props;

  const queryPath = useQueryPath();
  const fileCollectionQuery =
    queries.useGetFileCollectionWithContext(queryPath);

  const refetch = () => {
    fileCollectionQuery.refetch();
  };

  const alert = useAlert();

  const { save, isSaved } = useFileWriter({
    projectRelativeFilePath,
    hideButtons: true,
    initialWriterView: "edit",
  });

  const saveAction: ContextMenuItem = {
    getTitle: () => "ðŸ’¾ Save",
    getIsEnabled: () => !isSaved,
    onClick: async () => {
      await save();
      refetch();
    },
  };

  const copyAction: ContextMenuItem = {
    getTitle: () => "ðŸ–² Copy",
    onClick: () => {
      console.log("Coming soon");
    },
  };

  const openAction: ContextMenuItem = {
    getTitle: () => "â†©ï¸ Open with",
    onClick: async () => {
      alert?.(
        "Coming soon, open VSCode/TextEdit/Browser, or other programs for other files?"
      );

      if (!projectRelativeFilePath) return;

      const apiResult = await api.vscodeOpen({
        files: [{ projectRelativePath: projectRelativeFilePath }],
      });

      showStandardResponse(apiResult);
    },
  };
  const runTestAction: ContextMenuItem = {
    getTitle: () => "âš¡ï¸ Test",
    getIsEnabled: (projectRelativeFilePath) =>
      projectRelativeFilePath?.endsWith(".test.ts") || false,
    onClick: () => {
      alert?.("Coming soon (open test with nodejs)");
    },
  };

  const exportAction: ContextMenuItem = {
    getTitle: () => "â¬‡ï¸ Export",
    onClick: () => {
      alert?.("Export (pdf, presentation-pdf)");
    },
  };

  const runFunctionAction: ContextMenuItem = {
    getTitle: () => "âš¡ï¸ Run",
    onClick: () => {
      alert?.(
        "Run file-action on this file, like generating an image description or sending it via an email"
      );
    },
  };

  const allActions = [
    saveAction,
    openAction,
    copyAction,
    runTestAction,
    exportAction,
    runFunctionAction,
  ];

  return (
    <Div className="flex flex-row flex-wrap">
      {allActions.map((action) => (
        <ContextMenuItemComponent
          action={action}
          projectRelativeFilePath={projectRelativeFilePath}
        />
      ))}
    </Div>
  );
}
