(props: { projectRelativeFilePath: string }) => {
  const { projectRelativeFilePath } = props;

  const { renderFileWriter } = useFileWriter({
    projectRelativeFilePath,
    hideButtons: true,
    initialWriterView: "edit",
  });

  return (
    <Div className="flex flex-1 flex-col min-h-[80vh]">
      <FileActions projectRelativeFilePath={projectRelativeFilePath} />
      {renderFileWriter()}
    </Div>
  );
}
