value:
(props: {
  item: ContextualPrompt;
  markdown?: string | null;
  contextSelection?: string | null;
  projectRelativeFilePath?: string;
}) => {
  const { item, markdown, projectRelativeFilePath, contextSelection } = props;
  const router = useRouter();
  const showPromptAlert = usePromptResultAlert();
  const alert = useAlert();
  const title = `${item.isFavorite ? "â­ï¸ " : ""}${item.title || item.name}`;

  const refetch = () => {
    alert?.("No refetch");
  };
  const projectRelativeFolderPath = getFolderJs(projectRelativeFilePath);
  return (
    <Tooltip
      hoverTimeout={100}
      tooltip={
        <Div>
          <P className="font-bold text-xl">{title}</P>
          {item.description ? (
            <MarkdownContent content={item.description} config={{}} />
          ) : null}
          <br />

          <ClickableIcon
            emoji={
              item.isFavorite
                ? "â­ï¸ Favorite (click to change)"
                : "ðŸ’© Not favorite (click to change)"
            }
            onClick={async () => {
              // go to upsert ContextualPrompt db page.
              const apiResult = await api.upsertDbModelWithContext(
                "ContextualPrompt",
                {
                  ...item,
                  isFavorite: !item.isFavorite,
                }
              );

              showStandardResponse(apiResult);

              refetch();
            }}
          />

          <ClickableIcon
            emoji="âœï¸ Edit"
            onClick={() => {
              // go to upsert ContextualPrompt db page.
              router.push(`/edit/ContextualPrompt?id=${item.id}`);
            }}
          />

          <ClickableIcon
            emoji="ðŸ—‘ Delete"
            onClick={async () => {
              alert?.(
                "Are you sure?",
                "You're going to remove this prompt from your database",
                [
                  {
                    text: "Do it",
                    style: "destructive",
                    onPress: async () => {
                      const apiResult = await api.deleteDbModelWithContext(
                        "ContextualPrompt",
                        item.id
                      );

                      showStandardResponse(apiResult);

                      if (apiResult?.result?.isSuccesful) {
                        refetch();
                      }
                    },
                  },
                  {
                    text: "Cancel",
                    style: "cancel",
                  },
                ]
              );
              // delete prompt
            }}
          />

          {contextSelection ? null : (
            <ClickableIcon
              emoji="ðŸ’¥ Apply on folder"
              onClick={() => {
                if (!projectRelativeFolderPath) return;

                alert?.(
                  "Apply on folder",
                  `You're going to apply the prompt ${item.name} on all files in the folder. Are you sure?`,
                  [
                    { style: "cancel", text: "cancel" },
                    {
                      style: "default",
                      text: "Yes",
                      onPress: async () => {
                        const apiResult =
                          await api.processPromptOnFolderWithContext({
                            projectRelativeFolderPath,
                            promptSlug: item.slug,
                            isRecursive: false,
                          });

                        showStandardResponse(apiResult);
                      },
                    },
                    {
                      style: "destructive",
                      text: "Yes, recursive",
                      onPress: async () => {
                        const apiResult =
                          await api.processPromptOnFolderWithContext({
                            projectRelativeFolderPath,
                            promptSlug: item.slug,
                            isRecursive: true,
                          });

                        showStandardResponse(apiResult);
                      },
                    },
                  ]
                );
              }}
            />
          )}
        </Div>
      }
      placement="top"
    >
      <NavButton
        onClick={async () => {
          if (!markdown) return;
          await processPrompt({
            contextualPromptSlug: item.slug,
            showPromptAlert,
            contextualContent: {
              contextContent: markdown,
              contextSelection,
              context_projectRelativeFilePath: projectRelativeFilePath,
            },
          });

          router.replace({
            query: {
              queryPath: router.query.queryPath,
              variant: item.slug,
            },
          });
        }}
        title={item.title || item.name}
      />
    </Tooltip>
  );
}
