value:
(props: {
  item: FileCollectionFile;
  index: number;
}) => {
  const { index, item } = props;

  const isMarkdown = item.name.endsWith(".md");
  const isCode = codeExtensions
    .concat(["json", "JSON"])
    .includes(getExtension(item.projectRelativePath));

  const tabs: TabType[] = [
    {
      emoji: "âœï¸",
      title: "Write",
      renderTab: () => {
        //  if (isCode) {
        // TODO: ts(x)/json files should be specially opened with the monaco editor to immediately get color highlighting and are editable. Never really interested in just viewing that one
        //   return <Div>Coming soon (MONACO)</Div>;
        // }
        return (
          <FileWriter projectRelativeFilePath={item.projectRelativePath} />
        );
      },
    },
    isMarkdown
      ? {
          //
          emoji: "ðŸ“–",
          title: "Read",
          renderTab: () =>
            item.rawContent ? (
              <MarkdownContent
                config={{
                  projectRelativeBaseFolderPath: getFolderJs(
                    item.projectRelativePath
                  ),
                  projectRelativeMarkdownFilePath: item.projectRelativePath,
                }}
                content={item.rawContent}
              />
            ) : null,
        }
      : undefined,
    isMarkdown
      ? {
          emoji: "ðŸ“½",
          title: "Present",
          renderTab: () => {
            const markdownParse = item.rawContent
              ? mdToJsonParse(item.rawContent)
              : undefined;
            if (!markdownParse) {
              return null;
            }
            return (
              <MarkdownParsePresentation
                markdownParse={markdownParse}
                projectRelativeBaseFolderPath={getFolderJs(
                  item.projectRelativePath
                )}
                projectRelativeMarkdownFilePath={item.projectRelativePath}
              />
            );
          },
        }
      : undefined,
    isMarkdown
      ? {
          emoji: "â“",
          title: "Prompt",
          renderTab: () => {
            return (
              <PromptViewer
                markdown={item.rawContent}
                contextualPromptResults={[]}
                contextualPrompts={[]}
                projectRelativeFilePath={item.projectRelativePath}
              />
            );
          },
        }
      : undefined,
  ].filter(notEmpty);

  return (
    <Details summary={item.name} isClosed={index !== 0}>
      {
        tabs.length > 1 ? (
          <Tabs isSmall tabKey={`textFile.${item.name}`} tabs={tabs} />
        ) : null
        //  tabs[0]?.renderTab()
      }
    </Details>
  );
}
