(props: { isSmall?: boolean }) => {
  const { isSmall } = props;
  const [placeholder, setPlaceholder] = useState(placeholders[0]);
  useState<string | null>(null);

  const [searchQuery, setSearchQuery] = useStore("menu.search");
  const router = useRouter();

  const go = () => {
    if (searchQuery.length === 0) return;
    router.push(`/search/${searchQuery}`);
  };
  return (
    <Div
      className={`rounded-full border border-zinc-700 dark:border-zinc-300 p-2 px-4 ${
        isSmall ? "text-sm my-2" : "text-3xl"
      } hover:bg-zinc-200 dark:hover:bg-zinc-800 bg-zinc-100 bg-opacity-75 dark:bg-zinc-600 dark flex flex-row`}
    >
      <Form
        onSubmit={(e) => {
          e.preventDefault();
          go();
        }}
      >
        <Input
          placeholder={placeholder}
          className="flex px-3 flex-1 bg-transparent focus:outline-none"
          value={searchQuery}
          onClick={() => setPlaceholder(pickRandomArrayItem(placeholders))}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
      </Form>

      <Div onClick={go} className="cursor-pointer">
        ðŸ”Ž
      </Div>
    </Div>
  );
}
